package io.hischoolboy.acl.config;import io.hischoolboy.acl.enums.LogType;import io.hischoolboy.acl.service.SysAclModuleService;import io.hischoolboy.acl.service.SysAclService;import io.hischoolboy.acl.service.SysDeptService;import io.hischoolboy.acl.service.SysRoleAclService;import io.hischoolboy.acl.service.SysRoleService;import io.hischoolboy.acl.service.SysRoleUserService;import io.hischoolboy.acl.service.SysService;import io.hischoolboy.acl.service.SysUserService;import io.hischoolboy.common.SpringHelper;import com.google.common.collect.ImmutableMap;import java.util.Map;public class LogTypeServiceConfig {    private final static Map<Integer, SysService> logTypeServiceMap = ImmutableMap.<Integer, SysService>builder()            .put(LogType.ACL.getCode(), SpringHelper.popBean(SysAclService.class))            .put(LogType.ACL_MODULE.getCode(), SpringHelper.popBean(SysAclModuleService.class))            .put(LogType.DEPT.getCode(), SpringHelper.popBean(SysDeptService.class)).put(LogType.USER.getCode(), SpringHelper.popBean(SysUserService.class))            .put(LogType.ROLE.getCode(), SpringHelper.popBean(SysRoleService.class))            .put(LogType.ROLE_USER.getCode(), SpringHelper.popBean(SysRoleUserService.class))            .put(LogType.ROLE_ACL.getCode(), SpringHelper.popBean(SysRoleAclService.class)).build();    public static SysService getByLogTypeCode(int code) {        return logTypeServiceMap.get(code);    }}