package io.hischoolboy.acl.convert;import io.hischoolboy.acl.domain.SysBase;import io.hischoolboy.acl.domain.SysUser;import io.hischoolboy.acl.util.RequestHolder;import io.hischoolboy.exception.ParaException;import io.hischoolboy.util.BeanValidator;import io.hischoolboy.util.IpUtil;import org.apache.commons.collections.MapUtils;import javax.servlet.http.HttpServletRequest;import java.util.Map;public class BaseConvert {    public static SysBase of() {        SysUser sysUser = RequestHolder.getCurrentUser();        HttpServletRequest request = RequestHolder.getCurrentRequest();        return SysBase.builder().operator(sysUser.getUsername()).operateIp(IpUtil.getRemoteIp(request)).build();    }    public static void checkPara(Object para) throws ParaException {        Map<String, String> errors = BeanValidator.validateForObjects(para);        if (MapUtils.isNotEmpty(errors)) {            throw new ParaException(errors.toString());        }    }}