package io.hischoolboy.acl.convert;import io.hischoolboy.acl.domain.SysBase;import io.hischoolboy.acl.domain.SysDept;import io.hischoolboy.acl.vo.DeptPara;public class DeptConvert {    public static SysDept of(DeptPara para) {        BaseConvert.checkPara(para);        SysBase base = BaseConvert.of();        return SysDept.builder().id(para.getId()).name(para.getName()).seq(para.getSeq()).remark(para.getRemark()).parentId(para.getParentId())                .operator(base.getOperator()).operateIp(base.getOperateIp()).build();    }}