package io.hischoolboy.acl.convert;import io.hischoolboy.acl.domain.SysBase;import io.hischoolboy.acl.domain.SysRole;import io.hischoolboy.acl.vo.RolePara;import io.hischoolboy.exception.ParaException;public class RoleConvert {    public static SysRole of(RolePara para) throws ParaException {        BaseConvert.checkPara(para);        SysBase base = BaseConvert.of();        return SysRole.builder().id(para.getId()).status(para.getStatus()).name(para.getName()).supplierId(para.getSupplierId()).remark(para.getRemark())                .type(para.getType()).operator(base.getOperator()).operateIp(base.getOperateIp()).build();    }}