package io.hischoolboy.acl.convert;import io.hischoolboy.acl.domain.SysBase;import io.hischoolboy.acl.domain.SysUser;import io.hischoolboy.acl.vo.UserPara;public class UserConvert {    public static SysUser of(UserPara para) {        BaseConvert.checkPara(para);        SysBase base = BaseConvert.of();        return SysUser.builder().id(para.getId()).username(para.getUsername()).telephone(para.getTelephone()).mail(para.getMail()).remark(para.getRemark())                .deptId(para.getDeptId()).status(para.getStatus()).supplierId(para.getSupplierId()).managedSupplierIds(para.getManagedSupplierIds())                .operator(base.getOperator()).operateIp(base.getOperateIp()).build();    }}