package io.hischoolboy.business.service;import io.hischoolboy.business.dao.TestDao;import io.hischoolboy.business.domain.Test;import io.hischoolboy.datasource.DataSources;import io.hischoolboy.datasource.RoutingDataSource;import org.springframework.stereotype.Service;import javax.annotation.Resource;/** * Created by jimin on 16/3/22. */@Servicepublic class TestDataSourceService {    @Resource    private TestDao testDao;    @RoutingDataSource(DataSources.LOG)    public void save(String msg) {        Test test = Test.builder().text(msg).build();        testDao.save(test);    }}